     select  
       *
      from
      (
      
	    
	    where u.id = b.user_id and to_char(end_date,'yyyy-MM-dd')>=#{endDate}
	    
        and   u.MEMBER_CARD like '%${member_card}%'
        
        
          and u.MERCHANT_NAME like '%${merchant_name}%'
        
        
          and u.MOBILE like '%${mobile}%'
        
        
          and  b.STATUS = #{status}
        
	     ) A 
	      
             where ROWNUM <= #{limitEnd}
          
	     ) 
	 
         where RN > #{limitStart}
    
    
      order by ${orderByClause}

    
