select sum(case when business_type='00' then tran_amt else null end) sumTx, sum(case when business_type='01' then tran_amt else null end) sumCz, sum(case when business_type='02' then tran_amt else null end) sumXf, sum(case when business_type='03' then tran_amt else null end) sumTk
from wx_shop_bussiness where 1=1
and BUSINESS_TYPE = #{business_type}
and MERCHANT_NAME like '%${merchant_name}%'
and BUSINESS_RESULT = #{business_result}
and SERIAL like '%${serial}%'
and SOURCE_SERIAL like '%${source_serial}%'
and MERCHANT_CODE = #{merchant_code}
and BUSINESS_TIME between to_date('${strDate}','yyyy-mm-dd hh24:mi:ss') and to_date('${endDate}','yyyy-mm-dd hh24:mi:ss')
order by ${orderByClause}

    
