mapper.select:
    
     select * from
      (select F.*,ROWNUM RN
       from (
       select * from (
       select * from (select A.MERCHANT_CODE,A.create_time,A.check_result,A.review_result,E.merchant_name,A.sumOrderAmt,A.sumPound,
       A.sumTranAmt,A.yhAmt,A.cardAmt,A.cardNum,A.wetAmt,A.wetNum,A.zfbAmt,A.zfbNum
      from (
       select MERCHANT_CODE,to_char(create_time,'yyyy-MM-dd') create_time,check_result,review_result,
			sum(order_amt) sumOrderAmt,
			sum(poundage) sumPound,
			sum(tran_amt) sumTranAmt,
		    (sum(order_amt)-sum(tran_amt)) yhAmt,
			sum(case when business_type='00' then order_amt else null end) cardAmt,
		    count(case when business_type='00' then 1 else null end) cardNum,
			sum(case when business_type='01' then order_amt else null end) wetAmt,
			count(case when business_type='01' then 1 else null end) wetNum,
			sum(case when business_type='02' then order_amt else null end) zfbAmt,
			count(case when business_type='02' then 1 else null end) zfbNum
			
			from wx_check_bussiness 
			group by MERCHANT_CODE,to_char(create_time,'yyyy-MM-dd'),check_result,review_result
	   ) A
			left join wx_member_public E on E.MERCHANT_CODE = A.MERCHANT_CODE
			)      
	    
	      
	    
	     )
          where 1=1
          
             
             
             and CREATE_TIME between '${strDate}'
             and  '${endDate}'
          
         
	     ) F
	      
             where ROWNUM <= #{limitEnd}
          

	     )  
	 
         where RN > #{limitStart}
    
    
      order by ${orderByClause}


select count(*) from (
    select * from (
      select A.MERCHANT_CODE,A.create_time,A.check_result,A.review_result,E.merchant_name from 
    (select MERCHANT_CODE,create_time,check_result,review_result from wx_check_bussiness 
    
      group by
    MERCHANT_CODE,create_time,check_result,review_result 
      ) A
    left join wx_member_public E on E.MERCHANT_CODE = A.MERCHANT_CODE
    )
      
         
      
      )
