mapper.select:
    
     select  
     
      from
      (
      select A.*,ROWNUM RN
       from (  
	    select
	    
	      distinct
	    
	    
	    
	    from WX_CHECK_ERROR 
	    
	      
	    
	     ) A 
	      
             where ROWNUM <= #{limitEnd}
          

	     )
	     
	 
         where RN > #{limitStart}
    
    
    
      order by ${orderByClause}
