mapper.select:
    
     select  
     *
      from
      (
      select A.*,ROWNUM RN
       from (  
	    select
	    
	      distinct
	    
	    
	    
	    from WX_USERS_BUSSINESS  
	    
	      
	    
	     ) A 
	      
             where ROWNUM <= #{limitEnd}
          

	     )
	     
	 
         where RN > #{limitStart}
    
    
    
      order by ${orderByClause}

select
from WX_USERS_BUSSINESS where business_source ='00' and business_result='01' and to_char(BUSINESS_TIME,'yyyy-mm-dd') = #{businessTime} and status='01' and business_type !='03'
order by ${orderByClause}

mapper.update:

update WX_USERS_BUSSINESS

  
    STATUS = #{STATUS,jdbcType=VARCHAR},
  

where to_char(BUSINESS_TIME,'yyyy-mm-dd') = #{BUSINESS_TIME,jdbcType=VARCHAR}
and MERCHANT_CODE=#{MERCHANT_CODE,jdbcType=VARCHAR}
